package class3new;

public class Upercase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char ch;
		for(ch='A';ch<='Z';ch++) {
			System.out.println(" "+ch);
		}
		System.out.println("Upercase");
		
		
		for(ch='a';ch<='z';ch++) {
			System.out.print(" "+ch+" ");
		}
		System.out.println("==Lowercase");
	}

}
